import osimport sysfrom os.path import join as join_pathcur_path = os.path.dirname(os.path.realpath(__file__))sys.path.append(join_path(cur_path, '..'))from om.mail_send import MailSendfrom om.configuration import JobConfigfrom om.om_job_runner import OmJobfrom common.alert.alert import PricingAlertfrom common.util.utils import *def _get_config_env():    config_env = 'SED_REPLACE_CONFIG_ENV'    if config_env.startswith('SED_REPLACE'):        config_env = 'local'    return config_envif __name__ == "__main__":    logger = LogUtil.create_logger(join_path(cur_path, 'log'), 'log.txt')    LogUtil.set_cur_logger(logger)    job_config = JobConfig(_get_config_env())    job_config.set_mail_send(MailSend())    job = OmJob(job_config)    PricingAlert.run_job(job)